import React from 'react';
import PropTypes from "prop-types";
import { useFormik } from 'formik';
import * as yup from 'yup';
import { FormControl, Grid, Checkbox, Button } from "@material-ui/core";
import { Alert } from "@material-ui/lab";
import Textfield from "../../components/form/Textfield";
import * as sc from "./styles";
import { Required, MinNumber, MaxNumber, MinStr, MaxStr } from "../../constants/messages";


const validationSchema = yup.object({
    storePrimaryLocation: yup
        .string()
        .required(Required),
    storeAddress: yup
        .string()
        .required(Required),
    storeContactNumber: yup
        .string()
        .min(1, MinNumber("Contact Number", 1))
        .max(12, MaxNumber("Contact Number", 12))
        .required(Required),
    storeEmailAddress: yup
        .string()
        .email('Must be a valid email address')
        .required(Required),
    serveOnTables: yup
        .boolean()
        .required(Required),
    tablePrefix: yup
        .string()
        .min(1, MinStr("Table Prefix", 1))
        .max(3, MaxStr("Table Prefix", 3))
        .required(Required),
    tableNumbers: yup
        .number()
        .min(1, MinNumber("Table Number", 1))
        .max(50, MaxNumber("Table Number", 50))
        .required(Required),
});

const StoreSetup = ({ storeDetails, onSubmit, jumpToStep, finishSetup, isStoreSetup }) => {

    const formik = useFormik({
        initialValues: {
            ...storeDetails
        },
        validationSchema,
        enableReinitialize: true,
        onSubmit: (values) => {
            onSubmit(values);
        }
    });

    const hasStores = localStorage.getItem("storeadded");

    return (
        <div id="step2">
            { hasStores && !isStoreSetup &&
                <>
                    <Alert severity="info" elevation="2">You've already added stores. You can skip this step.</Alert>
                    <br />
                    <br />
                </>
            }

            <form onSubmit={formik.handleSubmit}>
                <Grid container spacing={3}>
                    <Grid item xs={6}>
                        <FormControl fullWidth>
                            <Textfield
                                name="storePrimaryLocation"
                                label="Store Primary Location Name"
                                placeholder="e.g Malviya Nagar"
                                value={formik.values.storePrimaryLocation}
                                onChange={formik.handleChange}
                                error={formik.touched.storePrimaryLocation && !!formik.errors.storePrimaryLocation}
                                helperText={formik.touched.storePrimaryLocation && formik.errors.storePrimaryLocation}
                            />
                        </FormControl>
                    </Grid>

                    <Grid item xs={6}>
                        <FormControl fullWidth>
                            <Textfield
                                name="storeAddress"
                                label="Store Address"
                                placeholder="e.g Street 4, Pan Road, Kolkata"
                                value={formik.values.storeAddress}
                                onChange={formik.handleChange}
                                error={formik.touched.storeAddress && !!formik.errors.storeAddress}
                                helperText={formik.touched.storeAddress && formik.errors.storeAddress}
                            />
                        </FormControl>
                    </Grid>

                    <Grid item xs={6}>
                        <FormControl fullWidth>
                            <Textfield
                                name="storeContactNumber"
                                label="Store Contact Number"
                                placeholder="e.g 97850XXXXX"
                                value={formik.values.storeContactNumber}
                                onChange={formik.handleChange}
                                error={formik.touched.storeContactNumber && !!formik.errors.storeContactNumber}
                                helperText={formik.touched.storeContactNumber && formik.errors.storeContactNumber}
                            />
                        </FormControl>
                    </Grid>
                    <Grid item xs={6}>
                        <FormControl fullWidth>
                            <Textfield
                                name="storeEmailAddress"
                                label="Store Email Address"
                                placeholder="e.g store_malviyanagar@domain.com"
                                value={formik.values.storeEmailAddress}
                                onChange={formik.handleChange}
                                error={formik.touched.storeEmailAddress && !!formik.errors.storeEmailAddress}
                                helperText={formik.touched.storeEmailAddress && formik.errors.storeEmailAddress}
                            />
                        </FormControl>
                    </Grid>
                    <Grid item xs={12}>
                        <sc.ServeTableHolder>
                            <Checkbox name="serveOnTables" color="primary" value={storeDetails.serveOnTables} onChange={formik.handleChange} />
                            <FormControl fullWidth>
                                Do you serve on tables?
                    </FormControl>
                        </sc.ServeTableHolder>
                    </Grid>
                    {formik.values.serveOnTables && <>
                        <Grid item xs={6}>
                            <FormControl fullWidth>
                                <Textfield
                                    name="tablePrefix"
                                    label="Table Prefix"
                                    placeholder="e.g MT"
                                    value={formik.values.tablePrefix}
                                    onChange={formik.handleChange}
                                    error={formik.touched.tablePrefix && !!formik.errors.tablePrefix}
                                    helperText={formik.touched.tablePrefix && formik.errors.tablePrefix}
                                />
                            </FormControl>
                        </Grid>
                        <Grid item xs={6}>
                            <FormControl fullWidth>
                                <Textfield
                                    name="tableNumbers"
                                    label="Number of Table"
                                    placeholder="e.g 10"
                                    value={formik.values.tableNumbers}
                                    onChange={formik.handleChange}
                                    error={formik.touched.tableNumbers && !!formik.errors.tableNumbers}
                                    helperText={formik.touched.tableNumbers && formik.errors.tableNumbers}
                                />
                            </FormControl>
                        </Grid>
                    </>}
                </Grid>

                <sc.ShopSteupActionTray noBottomMargin={isStoreSetup}>
                    {!isStoreSetup && <Button color="primary" variant="contained" type="button" onClick={() => jumpToStep(0)} className="mr20">
                        Prev
                    </Button>}

                    <Button color="primary" variant="contained" type="submit" className="mr20">
                        {isStoreSetup ? "Create Store" : "Finish Setup"}
                    </Button>

                </sc.ShopSteupActionTray>

                {hasStores && !isStoreSetup && <sc.ShopFinishLink>
                    <span onClick={finishSetup} className="pointer">
                        [ Take me to Dashboard ]
                    </span>
                </sc.ShopFinishLink>}
            </form>
        </div>
    );
}

StoreSetup.defaultProps = {
    jumpToStep: () => { },
    finishSetup: () => { },
    isStoreSetup: false
}

StoreSetup.propTypes = {
    storeDetails: PropTypes.shape({
        storePrimaryLocation: PropTypes.string.isRequired,
        storeAddress: PropTypes.string.isRequired,
        storeContactNumber: PropTypes.string.isRequired,
        storeEmailAddress: PropTypes.string.isRequired,
        serveOnTables: PropTypes.bool.isRequired
    }),
    isStoreSetup: PropTypes.bool,
    onSubmit: PropTypes.func.isRequired,
    jumpToStep: PropTypes.func,
    finishSetup: PropTypes.func,
}

export default StoreSetup;
