import { createAsyncThunk } from "@reduxjs/toolkit";
import { getMyShop, getMyShopStores, getCategories } from "../../apis";

/* shop thunk */
export const getShopnameThunk = createAsyncThunk(
    'get-shopname-thunk',
    async () => (await getMyShop())
);

export const getShopStoresThunk = createAsyncThunk(
    'get-shopstores-thunk',
    async () => (await getMyShopStores())
);

export const getCategoriesThunk = createAsyncThunk(
    'get-categories-thunk',
    async () => (await getCategories())
);
