import styled from 'styled-components';

export const SetupWrapper = styled.div`

`;


export const ShopSteupWrapper = styled.div`
    position: relative;
    .shop-setup-stepper {
        margin: 20px 0 0;
        background-color: #f7f7f7;
    }
`;

export const ShopSteupTitle = styled.div`
    font-size: 22px;
    font-weight: 600;
    margin: 20px 0;
    color: #444;
    display: flex;
    align-items: center;
    justify-content: center;
    letter-spacing: 3px;
`;

export const ShopSteupContent = styled.div`
    padding: 15px 140px;
    min-height: 100%;
    overflow-y: auto;
`;

export const ShopSteupActionTray = styled.div`
    display: flex;
    justify-content: center;
    padding: ${props => props.noBottomMargin ? "30px 0px 0px" : "30px 0px"};
`;

export const ServeTableHolder = styled.div`
    display: flex;
    align-items: center;
`;

export const ShopWelcomeText = styled.div`
    font-size: 25px;
    margin-bottom: 15px;
    color: #333;
    letter-spacing: 3px;
`;

export const ShopFinishLink = styled.div`
    text-align: center;
    letter-spacing: 2px;
    font-size: 22px;
`;
