import React, { useEffect } from "react";
import { Switch, useRouteMatch } from "react-router-dom";
import { useSelector, useDispatch } from "react-redux";
import Sidebar from "../components/sidebar";
import Header from "../components/header";
import Footer from "../components/footer";
import Snackbar from "../components/snackbar";
import Loader from "../components/loader";
import Dialog from "../components/dialog";

import * as sc from "./styles.js";

import { PrivateRoute } from "../routes/types";



function AfterLoginLayout() {
    const dispatch = useDispatch();
    const { path } = useRouteMatch();
    const appSelector = useSelector(state => state.application);
    const shopSelector = useSelector(state => state.shop);
    const hasShop = !!localStorage.getItem("shopname");

    return (
        <sc.AppWrapper className="wrapper">
            { hasShop &&
                <>
                    <Sidebar />
                    <sc.AppContent>
                        <Header stores={shopSelector.stores} />
                        <sc.AppMainContent>
                            <Switch>
                                <PrivateRoute component={StoreContainer} path={`${path}stores`} isUserLoggedIn dispatch={dispatch} />
                            </Switch>
                        </sc.AppMainContent>
                        <Footer />
                    </sc.AppContent>
                </>
            }

            {!hasShop && <sc.AppMainContent className="px0">
                <ShopSetupContainer />
            </sc.AppMainContent>}

            <Snackbar toastr={appSelector.toastr} />
            <Loader loading={appSelector.loading || shopSelector.loading} />
            <Dialog open={appSelector.modal.open} modalProps={appSelector.modal.modalProps} />
        </sc.AppWrapper>
    );
}

export default AfterLoginLayout;
