import React from 'react';
import PropTypes from "prop-types";
import { useFormik } from 'formik';
import * as yup from 'yup';
import { FormControl, Button } from "@material-ui/core";
import Textfield from "../../components/form/Textfield";
import * as sc from "./styles";
import { ShopWelcomeMessage } from '../../constants/messages';

const shopValidationSchema = yup.object({
    shopname: yup
        .string()
        .required('This field is required'),
});

const ShopSetup = ({ shopDetails, onSubmit, jumpToStep }) => {
    const formik = useFormik({
        initialValues: {
            ...shopDetails
        },
        validationSchema: shopValidationSchema,
        enableReinitialize: true,
        onSubmit: (values) => {
            onSubmit(values);
        }
    });

    return (
        <div id="step1">
            <form onSubmit={formik.handleSubmit}>
                <sc.ShopWelcomeText>
                    {ShopWelcomeMessage}
                </sc.ShopWelcomeText>

                <FormControl fullWidth>
                    <Textfield
                        name="shopname"
                        label="Your Shop Name"
                        variant="outlined"
                        placeholder="e.g Krazy Mugg"
                        inputProps={{
                            style: {
                                fontSize: "60px"
                            }
                        }}
                        value={formik.values.shopname}
                        onChange={formik.handleChange}
                        error={formik.touched.shopname && !!formik.errors.shopname}
                        helperText={formik.touched.shopname && formik.errors.shopname}
                    />
                </FormControl>

                <sc.ShopSteupActionTray>
                    <Button color="primary" variant="contained" type="submit" className="mr20">
                        Save & Continue
                    </Button>

                    {shopDetails.shopId && <Button color="primary" variant="contained" type="button" onClick={() => jumpToStep(1)}>
                        Next
                    </Button>}
                </sc.ShopSteupActionTray>
            </form>
        </div>
    );
}

ShopSetup.propTypes = {
    shopDetails: PropTypes.shape({
        shopId: PropTypes.number.isRequired,
        shopname: PropTypes.string.isRequired,
    }),
    onSubmit: PropTypes.func.isRequired,
    jumpToStep: PropTypes.func.isRequired,
}

export default ShopSetup;
